import axios from 'axios'
import './App.css';
//import List from './component/list';

function App() {
      const [input, setInput] = useState('');

      const handleChange = e =>{
        setInput(e.target.value);
      };

      const handleSubmit = (e) =>{
        e.preventDefault();


        setInput('');
      };

      useEffect(() => {
        axios.get(``)
      })

    return(
     <div className="App"> 
          <h1>Todo</h1>  
        <div className='container'>

          <form onSubmit={handleSubmit}>
           <input type="text" value={input} onChange={handleChange} placeholder="Enter task"/>
           <button type='submite'><i className="fa fa-plus-circle" aria-hidden="true"></i></button>
          </form>

          <div id='output'>

              <h5> This property </h5>

              <div className='actions'>
                {/*<input type="checkbox" className='markdwon'/>*/}

                <button className='Update-btn'>Done</button>
                <button className='Update-btn'><i className="fa fa-pencil" aria-hidden="true"></i></button>
                <button className='Delete-btn'><i className="fa fa-trash" aria-hidden="true"></i></button>
              </div>
          </div>


       </div>
     </div> 
    )
}


export default App;

