        //setInput('');
      };

      useEffect(() => {
        axios.get(`http://localhost:8080/api/items`).then((res) =>{
          const respnseTodos = res.data;
          setTodos(respnseTodos);
        });
      }, []);


    return(
     <div className="App"> 
          <h1>Todo</h1>  
        <div className='container'>

          <form>

           <input type="text" value={item} 
                  onChange={(e)=>{setInput(e.target.value)}} 
                  placeholder="Enter Task" required/>

           <button onClick={AddItem}>
              <i className="fa fa-plus-circle" aria-hidden="true"></i>  
            </button>

          </form>

              {todos ? (
                todos.map((todo) => {
                  const {id, item, status} = todo;
                  return(<div id='output' key={id}>

                  <h5>{item}</h5>

                  <div className='actions'>
                    {/*<input type="checkbox" className='markdwon'/>*/}

                    <button className='Update-btn'>{`${status}`}</button>
                    <button className='Update-btn'><i className="fa fa-pencil" aria-hidden="true"></i></button>
                    <button className='Delete-btn'><i className="fa fa-trash" aria-hidden="true"></i></button>
                  </div>
                 </div>)
               })
              ) : ("loding...")
              } 

       </div>
     </div> 
    )
}


export default App;

