import React, { useEffect, useState } from 'react';
//import Tasks from './component/Tasks';
import axios from 'axios'
import './App.css';
//import List from './component/list';

function App() {
      const [input, setInput] = useState({
        item: ""
      });
      const [todos, setTodos] = useState('');
      const [addtodo, setAddTodo] = useState('')
      const [tododelete, setTodoDelete] = useState('');


      function handleChange(e){
        const newInput = {...input}
        newInput[e.target.id] = e.target.value
        setTodos(newInput)
      };

      function handleSubmit (e){
        e.preventDefault();
        axios.post(`http://localhost:8080/api/items`,{
          item: input.item
        })
        .then(res => console.log(res.input));

        setInput('');
      };

      useEffect(() => {
        axios.get(`http://localhost:8080/api/items`).then((res) =>{
          const respnseTodos = res.data;
          setTodos(respnseTodos);
        });
      }, []);


    return(
     <div className="App"> 
          <h1>Todo</h1>  
        <div className='container'>

          <form onSubmit={handleSubmit}>
           <input type="text" value={input.item} onChange={(e) => handleChange(e)} placeholder="Enter Task"/>
           <button type='submit'><i className="fa fa-plus-circle" aria-hidden="true"></i></button>
          </form>

              {todos ? (
                todos.map((todo) => {
                  const {id, item, status} = todo;
                  return(<div id='output' key={id}>

                  <h5>{item}</h5>

                  <div className='actions'>
                    {/*<input type="checkbox" className='markdwon'/>*/}

                    <button className='Update-btn'>{`${status}`}</button>
                    <button className='Update-btn'><i className="fa fa-pencil" aria-hidden="true"></i></button>
                    <button className='Delete-btn'><i className="fa fa-trash" aria-hidden="true"></i></button>
                  </div>
                 </div>)
               })
              ) : ("loding...")
              } 

       </div>
     </div> 
    )
}


export default App;

