              <h1>ToDo</h1>

              <form onSubmit={this.handleSubmit} className="flex"> 
                  <input type="text" 

                      placeholder="Enter your Taks"/>
                  <button type='submit'>Add</button>    
              </form>

              <div>
                {
                  items.map((items) =>(
                    <div key={items._id} className="flex items_container">
                        <input type="checkbox" 
                            checked={items.completed}
                            onClick={() => this.handleUpdate(items._id)} /> 

                        <div className={items.completed ? "items line_through" : "items"}>
                          {items.items}
                        </div> 

                      <button onClick={()=> this.handleDelete(items._id)}>Delete</button>     
                    </div>
                  ))
                }
              </div>
