import React, { useEffect, useState } from 'react';
import axios from 'axios'
import './App.css';


function App() {
      const [item, setItem] = useState('');
      const [todos, setTodos] = useState('');
      const [todoEditing, setTodoEditing] = useState(null);
      const [editingText, setEditingText] = useState("")



     function getList(){
      axios.get(`http://localhost:8000/api/items`).then((res) =>{
        const respnseTodos = res.data;
          setTodos(respnseTodos);
       });
    }

    useEffect(() => {
      getList()
    }, []);

    const AddItem = (e) =>{ 
      e.preventDefault();
        axios.post(`http://localhost:8000/api/items`,{
           item: item 
        }).then(res => {
          console.log('adding item', res)
          getList()
        })
        .catch(err => console.log(err))
        setItem('');     
    };  

      function deleteItem(id){ 
        axios.delete(`http://localhost:8000/api/items/${id}`)
        .then(res => {
          console.log('Deleted item', res)
          getList()
        })
        .catch(err => console.log(err))
      };

      const updateItem = async(id, todo) => { 
        const updatedItem  =[...todos].map((todo) =>{
          if (todo.id === id) {
            axios.put(`http://localhost:8000/api/items/${id}`,{
              item: editingText
            })
            .then(res => {
            console.log('updated item', res)
           getList()
        })
        .catch(err => console.log(err))
          }
          return todo
        })
        setItem(todo.item)
        setTodos(updatedItem)
        setTodoEditing(null)
        setEditingText("")
        getList()         
      };

      function updateStatus (todo){

        const data = {id:todo.id, status:!todo.status};
        axios.put(`http://localhost:8000/api/items/${data.id}`, data)
        .then(() => {
          const newTodos = todos.map(t => {
            if (t.id === t.id) {
               t.status = !t.status;
            }
           return t;

          });
         setTodos([...newTodos]);
          getList()
        });

      }

    return(
     <div className="App"> 
          <h1>Todo</h1>  
        <div className='container'>

          <form>

           <input type="text" required value={item} 
                  onChange={(e)=>setItem(e.target.value)} 
                  placeholder="Enter Task"/>

           <button onClick={AddItem} className="submit-btn"> 
           <i className="fa fa-plus" aria-hidden="true"></i>
            </button>

          </form>

              {todos ?  (
                todos.map((todo) => {
                  const {id, item, status, createdAt} = todo;
                  return(<div id='output' key={id}>


                  <input type="checkbox" checked={todo.status}
                   onClick={() => updateStatus(todo, )}
                   defaultChecked={false}
                   />



                   {/* {todo.status ? <del>{todo.item}</del> && (

                todoEditing === todo.id ? (
                      <input  type="text" 
                      onChange={(e) => setEditingText(e.target.value)}
                      value={editingText}
                      />
                ) : (
                  <del>{item}</del>
                )
                  ): <h5>{item}</h5>} */}



                    {todo.status ? <del>{todo.item}</del> : ""}

                  {todoEditing === todo.id ? (
                        <input  type="text" 
                        onChange={(e) => setEditingText(e.target.value)}
                        value={editingText}
                        />
                  ) : (
                    <h5 style={{textDecoration: line ? "line-through" : "none"}}>{item}</h5>
                  )} 


                  <div className='actions'>

                      { todoEditing === todo.id ? (
                           <button className='Updated-btn' 
                           onClick={() => updateItem(todo.id)}>
                         Update
                       </button>
                      ) : (
                        <button className='Update-btn' 
                        onClick={() => setTodoEditing(todo.id)}>
                      <i className="fa fa-pencil" aria-hidden="true"></i>
                    </button>
                      )}




                    <button className='Delete-btn' onClick={() => deleteItem(todo.id)}>
                      <i className="fa fa-trash" aria-hidden="true"></i>
                      </button>
                  </div>
                 </div>)
               })
              ) : ("loding...")
              } 

       </div>
     </div> 
    )
}


export default App;
