import React, { useEffect, useState,useRef } from 'react';
import axios from 'axios'
import './App.css';


function App() {
      const [item, setItem] = useState('');
      const [todos, setTodos] = useState('');
      const [isUpdating, setIsUpdating] = useState('');
      const [updateTexItem, setUpdateTexItem] = useState('');

      //const [isCompleted, setIsCompleted] = useState(false);



      function getList(){
        axios.get(`http://localhost:8080/api/items`).then((res) =>{
          const respnseTodos = res.data;
          setTodos(respnseTodos);
        });
      }

      useEffect(() => {
        getList()
      }, []);

      const AddItem = (e) =>{ 
        e.preventDefault();
        axios.post(`http://localhost:8080/api/items`,{
            item: item
        }).then(res => {
          console.log('adding item', res)
          getList()
        })
        .catch(err => console.log(err))

        setItem('');

      };  

      function deleteItem(id){ 
        axios.delete(`http://localhost:8080/api/items/${id}`)
        .then(res => {
          console.log('Deleted item', res)
          getList()
        })
        .catch(err => console.log(err))
      };

      const updateItem = async (e) => {
        e.preventDefault()
        try {
          const res = await axios.patch(`http://localhost:8080/api/items/${isUpdating}`,{item: updateTexItem})
          setUpdateTexItem('');
          setIsUpdating('');
          console.log(res.data);
        } catch (err) {
          console.log(err);
        }
      }
      const renderUpdateForm = () => (
        <form onSubmit={(e) => {updateItem(e)}}>

           <input type="text" value={updateItem} name="item"  
                  onChange={(e) =>{setUpdateTexItem(e.target.value)}}  value={updateTexItem}/>

           <button type='submit' onClick={AddItem}>
              Update
            </button>

          </form>
      )


      const itemStatus = (todo) => {
          setTodos(
            todos.map((item) =>{
              if(item.id === todo.id){
                  return {...item, completed: !item.completed}
              }
            })
          )
      }

    return(
     <div className="App"> 
          <h1>Todo</h1>  
        <div className='container'>

          <form autoComplete='off'>

           <input type="text" value={item} name="item"  
                  onChange={(e)=>{setItem(e.target.value)}} 
                  placeholder="Enter Task" required="required"/>

           <button onClick={AddItem}>
              <i className="fa fa-plus-circle" aria-hidden="true"></i>  
            </button>

          </form>

              {todos  ( 
                todos.map((todo) => {
                  const {id, item, status, createdAt} = todo;
                  return(<div id='output' key={id}>
                    {
                      isUpdating === todo._id
                      ? renderUpdateForm()
                      :<>
                               <input type="checkbox" /><h5>{item}</h5>

                      <div className='actions'>

                        <button className='Update-btn' onClick={setIsUpdating(todo.id)}>
                          <i className="fa fa-pencil" aria-hidden="true"></i>
                        </button>


                        <button className='Delete-btn' onClick={() => deleteItem(todo.id)}>
                          <i className="fa fa-trash" aria-hidden="true"></i>
                          </button>
                      </div>
                      </>
                    }
                 </div>)
               })
              ) 
              } 

       </div>
     </div> 
    )
}


export default App;
