import React, { useEffect, useState } from 'react';
//import Tasks from './component/Tasks';
import axios from 'axios'
import './App.css';
//import List from './component/list';

function App() {
      const [item, setItem] = useState('');
      const [status, setStatus] = useState('');
      const [todos, setTodos] = useState('');
      const [updateitem, setUpdateItem] = useState({
        item
      })


      function getList(){
        axios.get(`http://localhost:8080/api/items`).then((res) =>{
          const respnseTodos = res.data;
          setTodos(respnseTodos);
        });
      }

      useEffect(() => {
        getList()
      }, []);

      const AddItem = (e) =>{ 
        e.preventDefault();
        axios.post(`http://localhost:8080/api/items`,{
            item: item
        }).then(res => {
          console.log('adding item', res)
          getList()
        })
        .catch(err => console.log(err))

        setItem('');

      };  

      function deleteItem(id){ 
        axios.delete(`http://localhost:8080/api/items/${id}`)
        .then(res => {
          console.log('Deleted item', res)
          getList()
        })
        .catch(err => console.log(err))
      };

      function updateItem(id){ 
        console.log(todos[id-1])
        setTodos(todos.item)
          setItem(todos.item);
        console.log(item);
        axios.patch(`http://localhost:8080/api/items/${id}`, {
            item
        }, {
          headers: { 'Content-type': 'application/json;' }
        })
        .then(res => {
          setItem(res.newItem)
          console.log('Updated item', res)
          getList()
        })
        .catch(err => console.log(err))


      };

    return(
     <div className="App"> 
          <h1>Todo</h1>  
        <div className='container'>

          <form autoComplete='off'>

           <input type="text" value={item} name="item"  
                  onChange={(e)=>{setItem(e.target.value)}} 
                  placeholder="Enter Task" required="required"/>

           <button onClick={AddItem}>
              <i className="fa fa-plus-circle" aria-hidden="true"></i>  
            </button>

          </form>

              {todos ? (
                todos.map((todo) => {
                  const {id, item, status, createdAt} = todo;
                  return(<div id='output' key={id}>


                  <input type="checkbox" />
                      <h5>{item}</h5>


                  <div className='actions'>

                    <button className='Update-btn'>{`${status}`}</button>

                    <button className='Update-btn' onClick={() => updateItem(todo.id)}>
                      <i className="fa fa-pencil" aria-hidden="true"></i>
                    </button>


                    <button className='Delete-btn' onClick={() => deleteItem(todo.id)}>
                      <i className="fa fa-trash" aria-hidden="true"></i>
                      </button>
                  </div>
                 </div>)
               })
              ) : ("loding...")
              } 

       </div>
     </div> 
    )
}


export default App;
