import React, { useEffect, useState } from 'react';
import axios from 'axios'
import './App.css';


function App() {
      const [item, setItem] = useState('');
      const [todos, setTodos] = useState('');
      const [todoEditing, setTodoEditing] = useState('');
      const [editingText, setEditingText] = useState("")
      //const [isCompleted, setIsCompleted] = useState([]);


     function getList(){
      axios.get(`http://localhost:8000/api/items`).then((res) =>{
        const respnseTodos = res.data;
          setTodos(respnseTodos);
       });
    }

      useEffect(() => {
      getList()
      }, []);

      const AddItem = () =>{ 
        //e.preventDefault();
        /**if(isUpdating === ""){
          axios.post("http://localhost:8000/api/items", {item})
          .then((res) => {
            console.log(res.data)
            setItem('');
            getList()
          })
          .catch((err) => console.log(err));
        }else{
          axios.post("http://localhost:8000/api/items", {id: isUpdating, item})
          .then((res) => {
            console.log(res.data)
            setItem('');
            setIsUpdating('');
            getList()
          })
          .catch((err) => console.log(err));
        } */

        axios.post(`http://localhost:8000/api/items`,{
           item: item
        }).then(res => {
          console.log('adding item', res)
          getList()
        })
        .catch(err => console.log(err))
        setItem('');

      };  

      function deleteItem(id){ 
        axios.delete(`http://localhost:8000/api/items/${id}`)
        .then(res => {
          console.log('Deleted item', res)
          getList()
        })
        .catch(err => console.log(err))
      };

      const updateItem = (id) => { 
        const updatedItem  =[...todos].map((todo) =>{
          if (todo.id === id) {
            axios.put(`http://localhost:8000/api/items/${id}`,{
              item: item
            })
            .then(res => {
            console.log('updated item', res)
           getList()
        })
        .catch(err => console.log(err))
          }
          return todo
        })
        setTodos(updatedItem)
        setTodoEditing(null)
        setEditingText("")
        getList()         
      };

      function itemStatus (id ,status)  {
          const itemDone  =[...todos].map((todo) =>{
            if (todo.id === id) {

              axios.put(`http://localhost:8000/api/items/${id}`,{
                status: status
              })
              todo.status = !todo.status
            }
            return todo
          })
          setTodos(itemDone)
      }

    return(
     <div className="App"> 
          <h1>Todo</h1>  
        <div className='container'>

          <form>

           <input type="text" value={item} 
                  onChange={(e)=>setItem(e.target.value)} 
                  placeholder="Enter Task" required="required"/>

           <button onClick={AddItem}> 
                Add
            </button>

          </form>

              {todos ? (
                todos.map((todo) => {
                  const {id, item, status, createdAt} = todo;
                  return(<div id='output' key={id}>


                  <input type="checkbox" 
                  //value={todo.id}
                   checked={todo.status}
                   onChange={() => itemStatus(todo.id)}
                   />

                  { todoEditing === todo.id ? (
                        <input  type="text" 
                        onChange={(e) => setEditingText()}
                        value={editingText}
                        />
                  ) : (
                    <h5>{item}</h5>
                  )}




                  <div className='actions'>

                    <button className='Update-btn' 
                        onClick={() => setTodoEditing(todo.id)}>
                      <i className="fa fa-pencil" aria-hidden="true"></i>
                    </button>

                    <button className='Update-btn' 
                        onClick={() => updateItem(todo.id)}>
                      Update
                    </button>

                    <button className='Delete-btn' onClick={() => deleteItem(todo.id)}>
                      <i className="fa fa-trash" aria-hidden="true"></i>
                      </button>
                  </div>
                 </div>)
               })
              ) : ("loding...")
              } 

       </div>
     </div> 
    )
}


export default App;
