//import Axios from 'axios'
import './App.css';

function App() {
    return(
     <div className="App"> 
          <h1>Todo</h1>  
        <div className='container'>

          <form>
           <input type="text" placeholder="Enter task"/>
           <button type='submite'><i class="fa fa-plus-circle" aria-hidden="true"></i></button>
          </form>

          <div id='output'>

              <h5>Taks</h5>

              <div className='actions'>
                <input type="checkbox" className='markdwon'/>
                <button className='Update-btn'><i className="fa fa-pencil" aria-hidden="true"></i></button>
                <button className='Delete-btn'><i className="fa fa-trash" aria-hidden="true"></i></button>
              </div>
          </div>


       </div>
     </div> 
    )
}


export default App;

/***
 * 
class App extends Tasks{
  state={ Tasks: [], currentTask: ""}
  render(){
    const {tasks} = this.state;
    return(
      <div className='App flex'>
          <div className='container'>
              <h1>ToDo</h1>

              <form onSubmit={this.handleSubmit} className="flex"> 
                  <input type="text" 
                      value={this.state.currentTask} 
                      required
                      onChange={this.handleChange}
                      placeholder="Enter your Taks"/>
                  <button type='submit'>Add</button>    
              </form>

              <div>
                {
                  tasks.map((task) =>(
                    <div key={task._id} className="flex task_container">
                        <input type="checkbox" 
                            checked={task.completed}
                            onClick={() => this.handleUpdate(task._id)} /> 

                        <div className={task.completed ? "task line_through" : "task"}>
                          {task.task}
                        </div> 

                      <button onClick={()=> this.handleDelete(task._id)}>Delete</button>     
                    </div>
                  ))
                }
              </div>
          </div>
      </div>
    );
  }
}


export default App;
